import * as types from "./actionTypes"

export const loadUsersStart = () => ({
  type: types.LOAD_USERS_START
})

export const loadUsersSuccess = (users) => ({
  type: types.LOAD_USERS_START,
  payload: users
})

export const loadUsersError = (error) => ({
  type: types.LOAD_USERS_ERROR,
  payload: error
})
