import { MDBBtn, MDBInput, MDBValidation } from "mdb-react-ui-kit"
import React, { useEffect, useState } from "react"
import { useDispatch, useSelector } from "react-redux"
import { useHistory, useParams } from "react-router"
import { v4 as uuidv4 } from "uuid"
import { addUserStart, updateUserStart } from "../redux/actions"
import { toast } from 'react-toastify'

const initialState = {
  id: uuidv4(),
  firstName: "",
  lastName: "",
  gender: "",
  email: "",
  address: "",
  phone: "",
  role: "",
}

const AddEditUser = () => {
  const [fromValue, setFromValue] = useState(initialState)
  const [editMode, setEditMode] = useState(false)
  const {users} = useSelector(state => state.data)
  const { firstName, lastName, gender, email, address, phone, role } = fromValue
  const history = useHistory()
  const dispatch = useDispatch()
  const {id} = useParams()

  useEffect(() => {
    if (id) {
      setEditMode(true)
      const singleUser = users.find(item => item.id === id)
      setFromValue({...singleUser})
    }
  }, [id])

  const handleSubmit = (e) => {
    e.preventDefault();
    if (firstName && lastName && gender && email && address && phone && role) {

      dispatch(addUserStart(fromValue))
      toast.success("Added Successfully")
      setTimeout(() => history.push("/"), 200)
    } else {
      dispatch(updateUserStart({id, fromValue}))
      setEditMode(false)
      toast.success("Updated Successfully")
      setTimeout(() => history.push("/"), 200)
    }
  };

  const onInputChange = (e) => {
    let { name, value } = e.target;
    setFromValue({ ...fromValue, [name]: value })
  }


  return (
    <MDBValidation
      className="row g-3"
      style={{ marginTop: "100px" }}
      noValidate
      onSubmit={handleSubmit}
    >
      <p className="fs-2 fw-bold">{!editMode ? "Add User" : "Edit User"}</p>
      <div
        style={{
          margin: "auto",
          padding: "15px",
          maxWidth: "400px",
          alignContent: "center",
        }}
      >
        <MDBInput
          value={firstName || ""}
          name="firstName"
          type="text"
          onChange={onInputChange}
          required
          label="First Name"
          validation="Please provide the first name"
          invalid
        />
        <br />
        <MDBInput
          value={lastName || ""}
          name="lastName"
          type="text"
          onChange={onInputChange}
          required
          label="Last Name"
          validation="Please provide the last name"
          invalid
        />
        <br />
        <MDBInput
          value={gender || ""}
          name="gender"
          type="text"
          onChange={onInputChange}
          required
          label="Gender"
          validation="Please provide gender"
          invalid
        />
        <br />
        <MDBInput
          value={email || ""}
          name="email"
          type="email"
          onChange={onInputChange}
          required
          label="Email"
          validation="Please provide email"
          invalid
        />
        <br />
        <MDBInput
          value={address || ""}
          name="address"
          type="text"
          onChange={onInputChange}
          required
          label="Address"
          validation="Please provide address"
          invalid
        />
        <br />
        <MDBInput
          value={phone || ""}
          name="phone"
          type="text"
          onChange={onInputChange}
          required
          label="Phone"
          validation="Please provide phone"
          invalid
        />
        <br />
        <MDBInput
          value={role || ""}
          name="role"
          type="text"
          onChange={onInputChange}
          required
          label="Role"
          validation="Please provide role"
          invalid
        />
        <br />
        <div className="col-12">
          <MDBBtn style={{ marginRight: "10px" }} type="submit">
            {!editMode ? "Add" : "Uppdate"}
          </MDBBtn>
          <MDBBtn onClick={() => history.push("/")} color="danger">
            Go Back
          </MDBBtn>
        </div>
      </div>
    </MDBValidation>
  );
};

export default AddEditUser;
